import React from 'react'
import { ChevronLeftIcon, ChevronRightIcon, chevron } from '@heroicons/react/24/outline'
import Image1 from '../assets/images/DORTMUND PART 2/DORTMUND KOLTUK TAKIMI/IMG_0001_19.jpeg'

const HeaderContent = () => {
  const images = [
    'path/to/image1.jpg',
    'path/to/image2.jpg',
    'path/to/image3.jpg',
    // ... diğer resim yolları
  ];
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentImageIndex(
      (prevIndex) => (prevIndex - 1 + images.length) % images.length
    );
  };
  return (
    <div className='w-full relative min-h-[80vh] my-2 p-10'>
      <div className="container m-auto flex items-center justify-center">
        <ChevronLeftIcon className='h-40 text-stone-500 hover:text-black ts cursor-pointer' />
        <img src={images[currentImageIndex]} className='object-cover shadow rounded min-w-[80%] max-h-[75vh]' alt="" />
        <ChevronRightIcon className='h-40 text-stone-500 hover:text-black ts cursor-pointer' />
      </div>
    </div>
  )
}

export default HeaderContent
