import React, { useEffect, useState } from 'react'
import { countD, countH, countW, countG } from '../store/reducers/exampleReducer'
import Header from '../components/Header'
import HeaderContent from '../components/HeaderContent'
import Footer from '../components/Footer'
import { configureStore, createSlice } from '@reduxjs/toolkit'
import { useSelector, useDispatch } from 'react-redux'
import { Link } from 'react-router-dom'
import { ChevronLeftIcon } from '@heroicons/react/24/outline'

const whatsappNumber = '905376919796';
const Article3 = () => {
  const userManagamentSlice = createSlice({
    name: "userManagament",
  });
  useEffect(() => {
    setTimeout(() => {
      window.location.href = "#article"
    }, 250);
  }, [])

  const store = configureStore({
    reducer: userManagamentSlice.reducer,
  });
  const { dortmund, countt } = useSelector((state) => state.userManagament);
  const dispatch = useDispatch();

  return (
    <div id='article'>
      <Header />
      <div className="w-full min-h-[90vh] max-h-[90vh] flex items-center h-full bg-[#f2f2f2] p-4">
        <div className="m-auto container grid grid-cols-2 bg-stone-200 grid h-full h-fit p-8">
          <div className={`flex h-[70vh]`}>
            <img src={dortmund[countt].id} className='h-full w-full object-cover' alt="" />
          </div>
          <div className="w-full h-full flex flex-col justify-between p-6 px-10 overflow-hidden">
            <div className="flex flex-col w-full">
              <span className='font-bold text-4xl'>Dortmund</span>
              <p className='mt-10 leading-6 text-sm'>SoovHome'un Dortmund koltuk takımı, rahatlık arayışınızı bir üst seviyeye taşıyor. Derin oturma alanları ve ergonomik tasarımı ile uzun sohbetlere, keyifli film gecelerine ve dinlendirici anlara ev sahipliği yapıyor. Gelişmiş teknolojilerle donatılmış yüksek kaliteli malzemeler, uzun ömürlü ve konforlu bir kullanım vadediyor.</p>
              <p className='mt-4 text-sm'>
                Dortmund koltuk takımı, şıklığı kadar dayanıklılığıyla da öne çıkıyor. SoovHome'un kalite standartlarına uygun olarak üretilen bu takım, uzun yıllar boyunca ilk günkü zarafetini ve sağlamlığını koruyarak sizlere uzun süreli bir kullanım sunuyor.
              </p>
            </div>
            <div className="w-full flex items-center justify-between">
              <Link to="/home" className="max-w-[40px] max-h-[40px] text-sm hover:bg-white hover:text-black cursor-pointer hover:max-h-[42.5px] hover:max-w-[42.5px] rounded-full bg-black text-white flex items-center justify-center p-3">
                <ChevronLeftIcon className='w-full h-full' />
              </Link>
              <div className="w-full flex justify-end items-center">
                <Link to={`https://wa.me/${whatsappNumber}`}  ><img src="assets/images/whatsapp-logo.png" className='max-w-[46px] max-h-[46px] w-full h-full cursor-pointer' alt="" /></Link>
                <Link to="https://www.facebook.com/soovhome/"><img src="assets/images/facebook-white.png" className='max-w-[40px] max-h-[40px] mx-6 cursor-pointer' alt="" /></Link>
                <Link to="https://www.instagram.com/soovhome/"><img src="assets/images/instagram-white.png" className='max-w-[36px] max-h-[36px] w-full h-full cursor-pointer' alt="" /></Link>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  )
}

export default Article3
