import React, { useState, useEffect } from 'react'
import { ChevronLeftIcon, ChevronRightIcon, chevron } from '@heroicons/react/24/outline'
import Image1 from '../assets/images/DORTMUND PART 2/DORTMUND KOLTUK TAKIMI/IMG_0001_19.jpeg'

const HeaderContent = () => {
  const images = [
    '/assets/images/DORTMUND PART 2/DORTMUND KOLTUK TAKIMI/IMG_0001_19.jpeg',
    '/assets/images/DORTMUND PART 2/DORTMUND KOLTUK TAKIMI/IMG_0001_20.jpeg',
    '/assets/images/DORTMUND PART 2/DORTMUND KOLTUK TAKIMI/IMG_0008_6.jpeg',

  ];
  const [currentImageIndexBefore, setCurrentImageIndexBefore] = useState(0);
  const [currentImageIndex, setCurrentImageIndex] = useState(1);
  const [currentImageIndexAfter, setCurrentImageIndexAfter] = useState(2);

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    setCurrentImageIndexBefore((prevIndex) => (prevIndex + 1) % images.length);
    setCurrentImageIndexAfter((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentImageIndex(
      (prevIndex) => (prevIndex - 1 + images.length) % images.length
    );
  };

  useEffect(() => {
    // Otomatik olarak 3 saniyede bir resmi değiştirmek için
    const interval = setInterval(() => {
      nextImage();
    }, 3000);

    // Component unmount olduğunda interval'i temizle
    return () => clearInterval(interval);
  }, [currentImageIndex]);

  return (
    <div className='w-full select-none relative min-h-[80vh] my-4 p-10'>
      <div className="container relative text-black m-auto flex items-center justify-center">
        <ChevronLeftIcon className='h-24 z-50 mr-10 hover:text-[#FFB000] text-[#FFCC70] ts cursor-pointer'
          onClick={prevImage}
        />
        <div className="flex w-full relative justify-center">
          <div className="absolute min-w-[110%] blur grid grid-cols-2">
            <img
              src={images[currentImageIndexBefore]}
              draggable="false"
              className='object-cover ts border z-5 rounded min-w-[100%] max-h-[60vh]'
              alt=""
            />
            <img
              src={images[currentImageIndexAfter]}
              draggable="false"
              className='object-cover ts border z-5 rounded min-w-[100%] max-h-[60vh]'
              alt=""
            />
          </div>
          <img
            src={images[currentImageIndex]}
            draggable="false"
            className='object-cover ts shadow rounded z-50 min-w-[90%] max-h-[70vh]'
            alt=""
          />
        </div>
        <ChevronRightIcon className='h-24 z-50 ml-10 hover:text-[#FFB000] text-[#FFCC70] ts cursor-pointer'
          onClick={nextImage}
        />
        <div className="l50 flex">
          <div className={`dot ${currentImageIndex === 0 ? 'active' : ''}`}></div>
          <div className={`dot ${currentImageIndex === 1 ? 'active' : ''}`}></div>
          <div className={`dot ${currentImageIndex === 2 ? 'active' : ''}`}></div>
        </div>
      </div>
    </div>
  )
}

export default HeaderContent
